  //   alignItems: 'center',
  //   marginBottom: theme.spacing.unit * 12,
  //   [theme.breakpoints.down("sm")]: {
  //     display: 'none',
  //   }
  // },
  // logotypeText: {
  //   fontWeight: 500,
  //   color: 'white',
  //   marginLeft: theme.spacing.unit * 2,
  // },
  // logotypeIcon: {
  //   width: 70,
