                                    onSelect={(value,option)=>this._select(value,option,onSubmit)}
                                    >
                                    <Search id="calendarSearch" placeholder={locale.Employee_name[language]} />
                                    </AutoComplete>
                                </TableCell>
                            </TableRow>
                        </TableHead>
                    </Table>
                    <div style={{textAlign:'-webkit-right', marginTop:8}}>
                    <Button style={{width:80}} onClick={this.handleSubmit} theme="outline">
                        {locale.Search[language]}
                    </Button>
                    </div>
                </div>
            </div>
          );
