import React, { Component } from 'react';
import ReactDOM from 'react-dom';
import 'antd/dist/antd.css';
import { Calendar, Badge,ConfigProvider,Modal   } from 'antd';
import koKR from 'antd/lib/locale-provider/ko_KR';
import './Calendar.css'

class CalendarClass extends Component {

  constructor(props, context) {
    super(props);

   this.state = {

        
    }
}

 getListData = (value,data) => {
  let listData=[];
  
  if(data.length > 0 )
  data.forEach((item)=>{
    var month="";
    var day ="";

    
    if( item.date.slice(5,6) === "0")
         month= item.date.slice(6,7)
    else
          month = item.date.slice(5,7)

      if( item.date.slice(8,9) === "0")
          day= item.date.slice(9,10)
     else
          day = item.date.slice(8,10)     


    if(value.month() == month-1 && value.date()==day)
    {
      listData.push({type:'success',content:item.location,data:{item}})
    }
  })


  return listData || [];
}

 dateCellRender=(value,data) => {
 const listData = this.getListData(value,data);
  return (
    <ul className="events">
      {listData.map(item => (
        <li key={item.content}>
          <Badge status={item.type} text={item.content} />
        </li>
      ))}
    </ul>
  );
}

 test=(value) => {
  console.log("onSelect");
 console.log(value);
 showModal()
}


 hideModal = () => {
  //visible=false;
  // this.setState({
  //   visible: false,
  // });
};



  render() {
    const{data} = this.props;

    return(
        <div>
          <Calendar dateCellRender={(value)=>this.dateCellRender(value,data)} onSelect={this.test}/>
          <Modal
            title="Modal"
            visible={false}
            // onOk={}
            onCancel={()=>{this.hideModal()}}
            okText="적용"
            cancelText="닫기"
          ></Modal>
        </div>
  
    );
  };
}

export default CalendarClass;
