import React from 'react';
import ReactDOM from 'react-dom';
import 'antd/dist/antd.css';
import { Calendar, Badge,ConfigProvider   } from 'antd';
import koKR from 'antd/lib/locale-provider/ko_KR';
import './Calendar.css'

function getListData(value,data) {
  let listData=[];

  data.forEach((item)=>{
    var date="";

    if( item.date.slice(5,6) === "0")
       date= item.date.slice(6,7)
    else
       date = item.date.slice(5,7)

    if(value.date() == date)
    {
      listData.push({type:'success',content:item.location,data:{item}})
      console.log(listData)
    }

  })


  return listData || [];
}

function dateCellRender(value,data) {
 const listData = getListData(value,data);
  return (
    <ul className="events">
      {listData.map(item => (
        <li key={item.content}>
          <Badge status={item.type} text={item.content} />
        </li>
      ))}
    </ul>
  );
}

function test(value) {
  console.log("onSelect");
 console.log(value);
}

 
function test3(value) {
  console.log("onPanelChange");
  console.log(value);
 }
 
function test4(value) {
  console.log("value");
  console.log(value);
 }

const CalendarClass = ({...props}) => {

    const {data} = props;
  return(
      <div>
        <Calendar dateCellRender={(value)=>dateCellRender(value,data)} onSelect={test} />,
      </div>

  );
};

export default CalendarClass;
