import React, { Component } from 'react';
import {Input} from 'antd';
import './chat.css';
import SockJsClient  from "react-stomp";
import UsernameGenerator from "username-generator";
import Fetch from "json-fetch";
import { TalkBox } from "react-talk";
import randomstring from"randomstring";


class chat extends Component {

    constructor(props, context) {
        super(props);

        this.randomUserName = UsernameGenerator.generateUsername("-");
        this.randomUserId = randomstring.generate();

       this.state = {
            messages : "",
            clientConnected:false,
            messages: [{
                "author": "Ponger",
                "authorId": "pong3",
                "message": "How you doin'!",
                "timestamp": Date.now().toString()
            },
            {
                "author": "Ponger2",
                "authorId": "pong4",
                "message": "zzz!",
                "timestamp": Date.now().toString()
            }
        
        ]
            
        }
    }
    


    //   componentWillMount() {
    //     console.log("call history");
    //     Fetch("/history", {
    //       method: "GET"
    //     }).then((response) => {
    //       this.setState({ messages: response.body });
    //     });
    //   }

          onMessageReceive = (msg, topic) => {
            //alert(JSON.stringify(msg) + " @ " +  JSON.stringify(this.state.messages)+" @ " + JSON.stringify(topic));
            this.setState(prevState => ({
              messages: [...prevState.messages, msg]
            }));
          }

          sendMessage = (msg, selfMsg) => {
            // selfMsg is the message object constructed from the message typed by the current user
            // NOTE: selfMsg doesn't include timestamp and needs to be added by the user of the module
            // in client or server side as required
            selfMsg["timestamp"] = Date.now().toString();
            this.setState(prevState => ({
                messages2: [...prevState.messages, selfMsg]
            }));
            // If message sending failed return false otherwise return true
            try {
              // Insert code to send the message below

              const s = {name: selfMsg.authorId, session: "0", content: msg}
              this.clientRef.sendMessage("/app/hello", JSON.stringify(s));
              
              return true;
            } catch (e) {
              return false;
            }
          }
      

    render() {

        const wsSourceUrl = "http://localhost:8076/chat";
         const { Search } = Input;
        return (
            <div>
            <TalkBox topic="/topic/testchat" currentUserId={this.randomUserId }
                currentUser={this.randomUserName} messages={this.state.messages}
               onSendMessage={ this.sendMessage } connected={this.state.clientConnected}
               onKeyDown={this.updateInputValue}
               />

            <SockJsClient
            url="http://localhost:8076/chat"
            topics={["/topics/testchat"]}
            onMessage={this.onMessageReceive }
            ref={(client) => { this.clientRef = client; }} 
            onConnect={ () => {this.setState({ clientConnected: true }) } }
            onDisconnect={ () => { this.setState({ clientConnected: false }) } }
            />
            </div>

        //     <div>
        //     <TalkBox topic="/topic/public" currentUserId={ this.randomUserId }
        //       currentUser={ this.randomUserName } messages={ this.state.messages }
        //       onSendMessage={ this.sendMessage } connected={ this.state.clientConnected }/>
            
        //     <SockJsClient url={ wsSourceUrl } topics={["/topic/public"]}
        //       onMessage={ this.onMessageReceive } ref={ (client) => { this.clientRef = client }}
        //       onConnect={ () => {this.setState({ clientConnected: true }) } }
        //       onDisconnect={ () => { this.setState({ clientConnected: false }) } }
        //       debug={ false } style={[{width:'100%', height:'100%'}]}/>
        //   </div>
            // <div>
            //     <div id="container">
            //         <div id="chatView">
            //             <div class="msg" id="chat-me">
            //                 정영석
            //             <li class="msgBox">채팅1</li>
            //             </div>
            //             <div class="msg" id="chat-other">
            //                 남영덕
            //             <li class="msgBox">채팅2</li>
            //             </div>
            //             <div class="msg" id="chat-other">
            //                 최기석
            //             <li class="msgBox">채팅3</li>
            //             </div>
            //         </div>
            //         <Search
            //                 placeholder="보낼 메시지를 입력하세요"
            //                 enterButton="보내기"
            //                 size="large"
            //                 onSearch={value => console.log(value)}
            //         />
            //     </div>  
            // </div>
        );
    }
}

export default chat;
