import React, { Component } from 'react';
import ReactDOM from 'react-dom';
import 'antd/dist/antd.css';
import { Calendar, Badge,ConfigProvider,Modal ,Input,TimePicker  } from 'antd';
import koKR from 'antd/lib/locale-provider/ko_KR';
import './Calendar.css'
import moment from 'moment';

class CalendarClass extends Component {

  constructor(props, context) {
    super(props);

    this.showModal = this.showModal.bind(this);

   this.state = {
    visible:false,
    startTime:null,
    endTime:null,
    location:null
    }
}

 getListData = (value,data) => {
  let listData=[];
  
  if(data && data.length > 0 )
  data.forEach((item)=>{
    var month="";
    var day ="";
    
    if( item.startDate.slice(5,6) === "0")
         month= item.startDate.slice(6,7)
    else
          month = item.startDate.slice(5,7)

      if( item.startDate.slice(8,9) === "0")
          day= item.startDate.slice(9,10)
     else
          day = item.startDate.slice(8,10)     

           
    if(value.month() == month-1 && value.date()==day)
    {
      
      var startTime=""
      var endTime=""
          if(item.startDate && item.startDate.length>12)
          startTime=item.startDate.slice(11,16);
            if(item.endDate && item.endDate.length>12)
            endTime=item.endDate.slice(11,16); 
      listData.push({type:'success',content:item.location,data:{item}})
      listData.push({type:'success',content:startTime+"~"+endTime,data:{item}})
    }
  })


  return listData || [];
}

 dateCellRender=(value,data) => {
 const listData = this.getListData(value,data);
  return (
    <ul className="events">
      {listData.map(item => (
        <li key={item.content}>
          <Badge status={item.type} text={item.content} />
        </li>
      ))}
    </ul>
  );
}

startTimeChange=(value)=>{
  console.log(value);

}

 test=(value) => {
  console.log("onSelect");
 console.log(value);

}


 hideModal = () => {
  //visible=false;
  this.setState({
    visible: false,
  });
};


 showModal = (value,data) => {
  this.setState({
    visible:true
  })
   let list = this.getListData(value,data);
   if(list && list.length>1){
    let splitData = list[1].content.split("~");
    this.setState({
      startTime:moment(splitData[0],"HH:mm"),
      endTIme:moment(splitData[1],"HH:mm"),
      location: list[0].content
    })
   }
  console.log("showModal")
 // visible=true;
  // this.setState({
  //   visible: true,
  // });
};
  render() {
    const{data} = this.props;


    return(
        <div>
          <Calendar dateCellRender={(value)=>this.dateCellRender(value,data)} onSelect={(value)=>this.showModal(value,data)}/>
          <Modal
            title="Modal"
            visible={this.state.visible}
             onOk={this.test()}
            onCancel={()=>{this.hideModal()}}
            okText="적용"
            cancelText="닫기"
          ><Input placeholder="Basic usage" value={this.state.location}/>
          <TimePicker  format={"HH:mm"} value={this.state.startTime} onChange={(value)=>this.startTimeChange(value)}/>~
          <TimePicker  format={"HH:mm"} value={this.state.endTime}/>
          </Modal>
        </div>
  
    );
  };
}

export default CalendarClass;
